$(document).ready(function () {
        $("#fc-selectable").fullCalendar({
            header: { left: "prev,next today", center: "title", right: "month,agendaWeek,agendaDay" },
            //defaultDate: "2016-06-12",
            selectable: !0,
            selectHelper: !0,
            select: function (t, e) {
                var a,
                    n = prompt("Event Title:");
                n && ((a = { title: n, start: t, end: e }), $("#fc-selectable").fullCalendar("renderEvent", a, !0)), $("#fc-selectable").fullCalendar("unselect");
            },
            editable: !0,
            eventLimit: !0,
            events: [
                { title: "All Day Event", start: "2021-10-01" },
                { title: "Long Event", start: "2021-10-07", end: "2021-10-10" },
                { id: 999, title: "Repeating Event", start: "2021-10-09T16:00:00" },
                { id: 999, title: "Repeating Event", start: "2021-10-16T16:00:00" },
                { title: "Conference", start: "2021-10-11", end: "2021-10-13" },
                { title: "Meeting", start: "2021-10-12T10:30:00", end: "2021-10-12T12:30:00" },
                { title: "Lunch", start: "2021-10-12T12:00:00" },
                { title: "Meeting", start: "2021-10-12T14:30:00" },
                { title: "Happy Hour", start: "2021-10-12T17:30:00" },
                { title: "Dinner", start: "2021-10-12T20:00:00" },
                { title: "Birthday Party", start: "2021-10-13T07:00:00" },
                { title: "Click for Google", url: "http://google.com/", start: "2021-10-28" },
            ],
        }),
       
       
    
        $.each($.fullCalendar.locales, function (t) {
            $("#lang-selector").append($("<option/>").attr("value", t).text(t));
        }),
        $("#lang-selector").on("change", function () {
            this.value && $("#fc-languages").fullCalendar("option", "locale", this.value);
        }),
        $("#fc-timezones").fullCalendar({
            header: { left: "prev,next today", center: "title", right: "month,agendaWeek,agendaDay" },
            //defaultDate: "2021-06-12",
            editable: !0,
            selectable: !0,
            eventLimit: !0,
            events: {
                url: "../../../app-assets/data/fullcalendar/php/get-events.php",
                error: function () {
                    $("#script-warning").show();
                },
            },
            loading: function (t) {
                $("#loading").toggle(t);
            },
            eventRender: function (t, e) {
                t.start.hasZone() && e.find(".fc-title").after($('<div class="tzo"/>').text(t.start.format("Z")));
            },
            dayClick: function (t) {
                console.log("dayClick", t.format());
            },
            select: function (t, e) {
                console.log("select", t.format(), e.format());
            },
        }),
        $.getJSON("../../../app-assets/data/fullcalendar/php/get-timezones.php", function (t) {
            $.each(t, function (t, e) {
                "UTC" != e && $("#timezone-selector").append($("<option/>").text(e).attr("value", e));
            });
        }),
        $("#timezone-selector").on("change", function () {
            $("#fc-timezones").fullCalendar("option", "timezone", this.value || !1);
        });
});
